// 메인에 표출되는 메모리스트 조회
export async function getMainMemos() {
  const response = await fetch("/api/memo/main");

  if (!response.ok) {
    const errorData = await response.json();
    throw errorData;
  }

  return response.json();
}

// 메모 조회
export async function getMemoById(memoId) {
  const response = await fetch(`/api/memo/{memoId}`);

  if (!response.ok) {
    const errorData = await response.json();
    throw errorData;
  }

  return response.json();
}
