import React from "react";

//props로 className, content, onClose, onEdit 받기
const MemoDetail = ({ memo }) => {
  if (!memo.content) return null;

  const formatDateSimple = (dateString) => {
    return dateString.replace("T", " ");
  };
  // 날짜 포맷팅
  const formattedDate = formatDateSimple(memo.createdAt);

  return (
    <div className="MemoDetail-backdrop">
      <div className={`MemoDetail-content ${memo.shape}`}>
        <div className="memo_info">
          <div className="writer-style">from. {memo.writer}</div>
          <div className="date-style">{memo.createdAt}</div>
        </div>
        <div className="content-style">{memo.content}</div>
        {/* <div className="MemoDetail-buttons">
          <button onClick={onEdit}>수정</button>
          <button onClick={onDelete}>삭제</button>
          <button onClick={onClose}>닫기</button>
        </div> */}
      </div>
    </div>
  );
};

export default MemoDetail;
