import React, { useState, useEffect } from "react";
import { useNavigate, useParams, useLocation } from "react-router-dom";
import axios from "axios";
import { useAuth } from "../components/AuthContext";

function MemoDetail({ memo }) {
  const params = useParams();
  const navigate = useNavigate();
  const location = useLocation();
  const goback = () => {
    navigate(-1);
  };

  const { getUserInfo } = useAuth();
  const [currentUser, setCurrentUser] = useState(null);
  const [authenticatedWriter, setAuthenticatedWriter] = useState(null);

  const [writer, setWriter] = useState("");
  const [password, setPassword] = useState("");
  const [content, setContent] = useState("");
  const [shape, setShape] = useState("square");
  const [color, setColor] = useState("beige");
  const [recipient, setRecipient] = useState(false);

  const [memoId, setMemoId] = useState(false);
  const [isEditing, setIsEditing] = useState(false);

  const maxWriterLength = 20;
  const maxLength = 255;

  useEffect(() => {
    const searchParams = new URLSearchParams(location.search);
    const editMode = searchParams.get("edit") === "true";

    if (editMode) {
      setIsEditing(true);
      axios
        .get(`/api/memo/update/${params.id}`)
        .then((res) => {
          const { writer, content, shape, color } = res.data;
          setWriter(writer);
          setContent(content);
          setShape(shape);
          setColor(color);
          setMemoId(params.id);
          // 비밀번호 관련 처리는 상황에 따라 다름
        })
        .catch((err) => console.error("Error fetching memo data:", err));
    } else {
      setRecipient(params.id);
    }

    // 페이지가 마운트될 때 사용자 정보 업데이트
    updateProfile();
  }, [params.id, location.search]);

  const updateProfile = async () => {
    const user = await getUserInfo();

    if (user) {
      setCurrentUser(user);
      setAuthenticatedWriter(user.id);
    } else {
      setCurrentUser(null);
    }
  };

  const handlePostitSubmit = async (e) => {
    e.preventDefault();

    const memoData = {
      writer,
      recipient,
      content,
      password,
      shape,
      color,
      authenticatedWriter,
    };

    const headData = {
      headers: {
        Authorization: "Bearer " + localStorage.getItem("access_token"),
        "Content-Type": "application/json",
      },
    };

    if (isEditing) {
      // 수정 로직
      axios
        .put(`/api/memo/${memoId}`, memoData, headData)
        .then((res) => {
          alert("포스트잇 수정을 완료하였습니다.");
          navigate(-1); // 또는 수정 후 보여줄 페이지로 이동
        })
        .catch((err) => {
          alert("포스트잇 수정이 실패하였습니다.");
          console.log(err);
        });
    } else {
      // 등록 로직
      axios
        .post("/api/memo", memoData, headData)
        .then((res) => {
          alert("포스트잇 등록을 완료하였습니다.");
          navigate(-1);
        })
        .catch((err) => {
          alert("포스트잇 등록을 실패하였습니다.");
          console.log(err);
        });
    }
  };

  return (
    <div className="wrap">
      <div className={`post-form-container ${color}`}>
        return (
        <div className="memo-detail">
          <h2>작성자: {memo.writer}</h2>
          <p>내용: {memo.content}</p>
          <p>작성일: {memo.createDt}</p>
          <p>모양: {memo.shape}</p>
        </div>
        );
      </div>
    </div>
  );
}

export default MemoDetail;
