import React, { useEffect, useState, useCallback } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import keyboard from '../images/keyboard.png';
import mouse from '../images/mouse.png';
import anonymous from '../images/anonymous-icon3.png';
import authenticated from '../images/authenticated-icon3.png';
import Modal from '../components/MemoDetail';
import PasswordModal from '../components/PasswordModal';
import keyboardBased from '../images/keyboard.png';
import mouseBased  from '../images/mouse.png';


function Memo() {
  const { id } = useParams(); // URLÏóêÏÑú id Í∞íÏùÑ Í∞ÄÏ†∏Ïò§Í∏∞
  const navigate = useNavigate(); // ÌéòÏù¥ÏßÄ ÎÑ§ÎπÑÍ≤åÏù¥ÏÖòÏùÑ ÏúÑÌïú Hook
  const [memos, setMemos] = useState([]); // Î©îÎ™® Î™©Î°ù ÏÉÅÌÉú Í¥ÄÎ¶¨
  const [name, setName] = useState(''); // ÏÇ¨Ïö©Ïûê Ïù¥Î¶Ñ ÏÉÅÌÉú Í¥ÄÎ¶¨
  const [isModalOpen, setIsModalOpen] = useState(false); // Î™®Îã¨ Ïó¥Î¶º/Îã´Ìûò ÏÉÅÌÉú Í¥ÄÎ¶¨
  const [modalContent, setModalContent] = useState(''); // Î™®Îã¨Ïóê ÌëúÏãúÎê† ÎÇ¥Ïö© Í¥ÄÎ¶¨
  const [modalDate, setModalDate] = useState(''); // Î™®Îã¨Ïóê ÌëúÏãúÎê† ÎÇ†Ïßú Í¥ÄÎ¶¨
  const [modalWriter, setModalWriter] = useState('');
  const [modalShape, setModalShape] = useState('');  // Î™®Îã¨ Î™®Ïñë(ÎîîÏûêÏù∏) ÏÉÅÌÉú Í¥ÄÎ¶¨
  const [editingMemoId, setEditingMemoId] = useState(null);//ÏàòÏ†ï ÏÉÅÌÉú Í¥ÄÎ¶¨
  const [isPasswordModalOpen, setIsPasswordModalOpen] = useState(false);

  const [keyboardUrl, setKeyboardUrl] = useState('');
  const [mouseUrl, setMouseUrl] = useState('');


  useEffect(() => {
    // Fetch user info
    const fetchUserInfo = async () => {
      try {
        const response = await fetch(`/members/${id}`);
        const data = await response.json();
        setName(data.name);
      } catch (error) {
        console.error("Error fetching user info:", error);
      }
    };

    // Fetch memos
    const fetchMemoData = async () => {
      try {
        const response = await fetch(`/api/memo/${id}`);
        if (!response.ok) throw new Error('Failed to fetch memos.');
        const data = await response.json();
        setMemos(data);
      } catch (error) {
        console.error("Error fetching memos:", error);
      }
    };

    const fetchKeyboardImage = async () => {
      try {
        const response = await fetch(`/auth/img/keyboard/${id}`);
        if (!response.ok) throw new Error('Failed to fetch keyboard image.');
        const blob = await response.blob();
        const imageUrl = URL.createObjectURL(blob);
        setKeyboardUrl(imageUrl);
      } catch (error) {
        setKeyboardUrl(keyboardBased); // Ïò§Î•ò Ïãú Î°úÏª¨ Ïù¥ÎØ∏ÏßÄ ÏÇ¨Ïö©
        console.error("Error fetching keyboard image:", error);
      }
    };

    const fetchMouseImage = async () => {
      try {
        const response = await fetch(`/auth/img/mouse/${id}`);
        if (!response.ok) throw new Error('Failed to fetch mouse image.');
        const blob = await response.blob();
        const imageUrl = URL.createObjectURL(blob);
        setMouseUrl(imageUrl);
      } catch (error) {
        setMouseUrl(mouseBased); // Ïò§Î•ò Ïãú Î°úÏª¨ Ïù¥ÎØ∏ÏßÄ ÏÇ¨Ïö©
        console.error("Error fetching mouse image:", error);
      }
    };

    fetchUserInfo();
    fetchMemoData();
    fetchKeyboardImage();
    fetchMouseImage();

  }, [id]);

  // Î©îÎ™® ÏûëÏÑ± ÌéòÏù¥ÏßÄÎ°ú Ïù¥ÎèôÌïòÎäî Ìï®Ïàò
  const goToCreateMemo = () => {
    navigate(`/postit/${id}/`);
  };

  // Ìôà ÌéòÏù¥ÏßÄÎ°ú Ïù¥ÎèôÌïòÎäî Ìï®Ïàò
  const goToHome = () => {
    navigate('/');
  };

  // ÌîÑÎ¶∞Ìä∏
  const getPrintPaper = () => {
    if (memos) {
      navigate('/print', { state: { memos: memos, name: name } });
    }
  }


  // Î©îÎ™® ÌÅ¥Î¶≠ Ìï∏Îì§Îü¨ Ìï®Ïàò
  const handleMemoClick = (memo) => {
    setModalDate(memo.date);
    setModalContent(memo.content);
    setModalWriter(memo.writer)
    setEditingMemoId(memo.id);
    setIsModalOpen(true);
    setModalShape(memo.shape);
  };

  // Î™®Îã¨ Îã´Í∏∞ Ìï®Ïàò
  const closeModal = () => {
    setIsModalOpen(false);
    setModalContent('');
    setEditingMemoId(null);
  };

  // Î©îÎ™® ÏàòÏ†ï ÌéòÏù¥ÏßÄÎ°ú Ïù¥ÎèôÌïòÎäî Ìï®Ïàò
  const handleEditClick = () => {
    navigate(`/postit/${editingMemoId}?edit=true`);
    setIsModalOpen(false);
  };

  // Î©îÎ™® ÏÇ≠Ï†ú Ìï®Ïàò ÏàòÏ†ï
  const handleDeleteClick = (memoId) => {
    setIsPasswordModalOpen(true);
    setEditingMemoId(memoId);
  };

  // ÎπÑÎ∞ÄÎ≤àÌò∏ Î™®Îã¨ÏóêÏÑú ÌôïÏù∏ÏùÑ ÎàåÎ†ÄÏùÑ Îïå Ïã§ÌñâÎê† Ìï®Ïàò
  const handlePasswordConfirm = async (password) => {
    console.log("ÏûÖÎ†•Îêú ÎπÑÎ∞ÄÎ≤àÌò∏:", password);
    console.log("ÏûÖÎ†•Îêú Î©îÎ™®id:", editingMemoId);
    // ÎπÑÎ∞ÄÎ≤àÌò∏ÏôÄ Î©îÎ™® IDÎ•º ÏÑúÎ≤ÑÏóê Ï†ÑÏÜ°
    try {
      const response = await fetch('/api/memo/delete', {
        method: 'POST', // Î©îÏÜåÎìúÎ•º DELETEÎ°ú Î≥ÄÍ≤Ω
        headers: {
          Authorization: 'Bearer ' + localStorage.getItem('access_token'),
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          memoId: editingMemoId, // ÏÇ≠Ï†úÌïòÎ†§Îäî Î©îÎ™®Ïùò ID
          password: password, // ÏÇ¨Ïö©ÏûêÍ∞Ä ÏûÖÎ†•Ìïú ÎπÑÎ∞ÄÎ≤àÌò∏
        }),
      });

      if (response.status === 202) {
        alert("Î©îÎ™®Î•º ÏÇ≠Ï†úÌïòÏòÄÏäµÎãàÎã§.");
        closeModal();
        setMemos(memos.filter(memo => memo.id !== editingMemoId));
      } else if (response.status === 401) {
        alert("ÎπÑÎ∞ÄÎ≤àÌò∏Î•º ÏûòÎ™ª ÏûÖÎ†•ÌïòÏòÄÏäµÎãàÎã§. ÎπÑÎ∞ÄÎ≤àÌò∏ Î¨∏ÏùòÎäî leesu@kcc.co.kr");
      } else {
        // Í∑∏ Ïô∏Ïùò Í≤ΩÏö∞, ÏùºÎ∞òÏ†ÅÏù∏ ÏóêÎü¨ Ï≤òÎ¶¨
        alert("Î©îÎ™® ÏÇ≠Ï†ú Ïã§Ìå®: Ïïå Ïàò ÏóÜÎäî ÏóêÎü¨ Î∞úÏÉù");
      }
    } catch (error) {
      console.error("Î©îÎ™® ÏÇ≠Ï†ú ÏöîÏ≤≠ Ï§ë Ïò§Î•ò Î∞úÏÉù", error);
      alert("Î©îÎ™® ÏÇ≠Ï†ú Í≥ºÏ†ïÏóêÏÑú Ïò§Î•òÍ∞Ä Î∞úÏÉùÌñàÏäµÎãàÎã§.");
      // ÎÑ§Ìä∏ÏõåÌÅ¨ Ïò§Î•ò ÎòêÎäî ÏöîÏ≤≠ Ïã§Ìå® Ï≤òÎ¶¨
    }

    // ÎπÑÎ∞ÄÎ≤àÌò∏ ÏûÖÎ†•Ïù¥ ÏôÑÎ£åÎêòÎ©¥ Î™®Îã¨ÏùÑ Îã´Ïùå
    setIsPasswordModalOpen(false);
  };




  return (
    <div className='wrap memo'>
      <h2>{name}ÏóêÍ≤å Ìè¨Ïä§Ìä∏ÏûáÏùÑ Î∂ôÏó¨Î¥ê!</h2>
      <div className='btns'>
        <button className='btn' onClick={goToHome}>üè† Home</button>
        <button className='btn btn-print' onClick={getPrintPaper}>üì© Print</button>
        <button className='btn' onClick={goToCreateMemo}>üìù Memo</button>
      </div>

      {/* Î©îÎ™® Î™©Î°ù ÌëúÏãú ÏòÅÏó≠ */}
      <div className='notebook'>
        {memos.length ? (
          <div className="notes-container">
            {memos.map(memo => (
              <div
                className={`note ${memo.shape} ${memo.color}`} // memo.shape Í∞íÏóê Îî∞Îùº 'note square' ÎòêÎäî 'note heart' ÌÅ¥ÎûòÏä§ Ï†ÅÏö©
                key={memo.id}
                onClick={() => handleMemoClick(memo)}
              >
                <div className='writer'>
                  {memo.authenticatedWriter > 0 && <img src={authenticated} alt="Authenticated" />}
                  {/* {!memo.authenticatedWriter && <img src={anonymous} alt="anonymous" />} */}
                  {memo.writer}
                </div>
                <div className='m-content'>{memo.content}</div>
              </div>
            ))}
          </div>
        ) : (
          <div className="empty-message">{name}ÎãòÍªò Ï≤´ Î≤àÏß∏ Î©îÎ™®Î•º ÎÇ®Í≤®Î≥¥ÏÑ∏Ïöî!</div>
        )}
      </div>

      {/* Î™®Îã¨Ïù¥ Ïó¥Î†∏ÏùÑ Îïå ÌëúÏãúÎê† Ïª®ÌÖêÏ∏† */}
      {isModalOpen && (
        <Modal
          className={`MemoDetail-content ${modalShape === 'heart' ? 'heart' : ''}`}
          content={modalContent}
          date={modalDate}
          writer={modalWriter}
          onClose={closeModal}
          onDelete={() => handleDeleteClick(editingMemoId)}
          onEdit={() => handleEditClick(editingMemoId)}
        />
      )}

      {isPasswordModalOpen && (
        <PasswordModal
          isOpen={isPasswordModalOpen}
          onClose={() => setIsPasswordModalOpen(false)}
          onConfirm={handlePasswordConfirm}
        />
      )}

      {/* ÌÇ§Î≥¥ÎìúÏôÄ ÎßàÏö∞Ïä§ Ïù¥ÎØ∏ÏßÄ */}
      <div className='km'>
        <img className='keyboard' src={keyboardUrl} alt="keyboard" />
        <img className='mouse' src={mouseUrl} alt="mouse" />
      </div>
    </div>
  );

}


export default Memo;
